{
    public class TestRobot
    {
        private const string TEST_XML = "<?xml version=\"1.0\" ?>\n<robot name=\"test_robot\" xmlns:xacro=\"http://ros.org/wiki/xacro\">\n  <!-- link list -->\n  <link name=\"base_link\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/base_link.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/base_link.dae\"/>\n      </geometry>\n      <material name=\"yellow\">\n        <color rgba=\"1 1 0 1\"/>\n      </material>\n    </visual>\n  </link>\n  <link name=\"link_1\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/link_1.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/link_1.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <link name=\"link_2\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/link_2.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/link_2.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <link name=\"link_3\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/link_3.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/link_3.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <link name=\"link_4\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/link_4.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/link_4.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <link name=\"link_5\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/link_5.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/link_5.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <link name=\"link_6\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/link_6.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/link_6.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <link name=\"tool0\"/>\n  <!--Cylinder and piston -->\n  <link name=\"link_cylinder\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/cylinder_link.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/cylinder_link.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <link name=\"link_piston\">\n    <collision name=\"collision\">\n      <geometry>\n        <mesh filename=\"package://collision/piston_link.stl\"/>\n      </geometry>\n    </collision>\n    <visual name=\"visual\">\n      <geometry>\n        <mesh filename=\"package://visual/piston_link.dae\"/>\n      </geometry>\n      <material name=\"yellow\"/>\n    </visual>\n  </link>\n  <!-- end of link list -->\n  <!-- joint list -->\n  <joint name=\"joint_1\" type=\"revolute\">\n    <origin rpy=\"0 0 0\" xyz=\"0 0 0.780\"/>\n    <axis xyz=\"0 0 1\"/>\n    <parent link=\"base_link\"/>\n    <child link=\"link_1\"/>\n    <limit effort=\"0\" lower=\"-2.967\" upper=\"2.967\" velocity=\"1.7453\"/>\n  </joint>\n  <joint name=\"joint_2\" type=\"revolute\">\n    <origin rpy=\"0 0 0\" xyz=\"0.320 0 0\"/>\n    <axis xyz=\"0 1 0\"/>\n    <parent link=\"link_1\"/>\n    <child link=\"link_2\"/>\n    <limit effort=\"0\" lower=\"-1.134\" upper=\"1.4855\" velocity=\"1.5707\"/>\n  </joint>\n  <joint name=\"joint_3\" type=\"revolute\">\n    <origin rpy=\"0 0 0\" xyz=\"0 0 1.075\"/>\n    <axis xyz=\"0 1 0\"/>\n    <parent link=\"link_2\"/>\n    <child link=\"link_3\"/>\n    <limit effort=\"0\" lower=\"-3.142\" upper=\"1.222\" velocity=\"1.5707\"/>\n  </joint>\n  <joint name=\"joint_4\" type=\"revolute\">\n    <origin rpy=\"0 0 0\" xyz=\"0 0 0.2\"/>\n    <axis xyz=\"1 0 0\"/>\n    <parent link=\"link_3\"/>\n    <child link=\"link_4\"/>\n    <limit effort=\"0\" lower=\"-5.236\" upper=\"5.236\" velocity=\"2.9671\"/>\n  </joint>\n  <joint name=\"joint_5\" type=\"revolute\">\n    <origin rpy=\"0 0 0\" xyz=\"1.392 0 0 \"/>\n    <axis xyz=\"0 1 0\"/>\n    <parent link=\"link_4\"/>\n    <child link=\"link_5\"/>\n    <limit effort=\"0\" lower=\"-2.094\" upper=\"2.094\" velocity=\"2.4435\"/>\n  </joint>\n  <joint name=\"joint_6\" type=\"revolute\">\n    <origin rpy=\"0 0 0\" xyz=\"0.2 0 0 \"/>\n    <axis xyz=\"1 0 0\"/>\n    <parent link=\"link_5\"/>\n    <child link=\"link_6\"/>\n    <limit effort=\"0\" lower=\"-6.283\" upper=\"6.283\" velocity=\"3.3161\"/>\n  </joint>\n  <joint name=\"joint_6-tool0\" type=\"fixed\">\n    <parent link=\"link_6\"/>\n    <child link=\"tool0\"/>\n    <origin rpy=\"0 1.57079632679 0\" xyz=\"0 0 0\"/>\n  </joint>\n  <joint name=\"joint_cylinder\" type=\"continuous\">\n    <origin rpy=\"0 0 0\" xyz=\"-0.3647 0 -0.1455\"/>\n    <axis xyz=\"0 1 0\"/>\n    <parent link=\"link_1\"/>\n    <child link=\"link_cylinder\"/>\n    <mimic joint=\"joint_2\" multiplier=\"-0.25\"/>\n  </joint>\n  <joint name=\"joint_piston\" type=\"continuous\">\n    <origin rpy=\"0 0 0\" xyz=\"-0.22 0 -0.0672\"/>\n    <axis xyz=\"0 1 0\"/>\n    <parent link=\"link_2\"/>\n    <child link=\"link_piston\"/>\n    <mimic joint=\"joint_2\" multiplier=\"-1.25\"/>\n  </joint>\n  <!-- end of joint list -->\n  <!-- ROS base_link to ABB World Coordinates transform -->\n  <link name=\"base\"/>\n  <joint name=\"base_link-base\" type=\"fixed\">\n    <origin rpy=\"0 0 0\" xyz=\"0 0 0\"/>\n    <parent link=\"base_link\"/>\n    <child link=\"base\"/>\n  </joint>\n</robot>\n\n";
        
        [Test]
        public void Deserialize_Success()
        {
            var serializer = new XmlSerializer(typeof(Preliy.Flange.Editor.XmlModel.Robot));
            using var reader = new StringReader(TEST_XML);
            var robot = (Preliy.Flange.Editor.XmlModel.Robot)serializer.Deserialize(reader);
            
            Assert.IsNotNull(robot);
            Assert.AreEqual("test_robot", robot.Name);
            Assert.AreEqual(11, robot.Links.Count);
            Assert.AreEqual(10, robot.Joints.Count);
        }
    }
}
